INSERT INTO `mq_quests` (`id`, `QuestID`, `Name`, `Description`, `Complete`, `FileName`, `MaxGold`, `MaxExp`) VALUES
(1, 1, 'Soluna City', 'First Town', 'none', 'towns/Soluna/town-SolunaCityNB-Frostval3015.swf', 0, 0),
(2, 2, 'University Test', 'This is your placement test at the university.  Score high for access to all the University has to offer.', 'Congratulations - You passed the test and made university history with a record breaking score!', 'towns/Gears/quest-exam2.swf', 1000, 1000),
(3, 3, 'Assault Mecha', 'Battle against other students applying or enrolled in GEARS University!', 'Good job! Now that you have tried your hand at the AssaultMecha arcade game, why not go battle a few real enemy mechas!', 'quests/quest-assaultmecha8.swf', 300000, 50000),
(4, 4, 'Rescue Sys-Zero', 'Sys-Zero, the dropship Captain from the first mission,  was captured during a top secret Recon mission to learn about the alien forces. He must be rescued at all cost!', 'You have rescued Sys-Zero and driven back the Shadowscythe forces that are attacking Planet Lore.', 'quests/quest-rescueSysZero.swf', 0, 0),
(5, 5, 'Intro Alpha', 'This is the Dropship that is heading to Soluna City, home of GEARS University!', '', 'towns/Intro/town-shuttle66.swf', 0, 0),
(6, 6, 'Soluna Battle', 'Random single battle quest based in Soluna City', 'Congratulations!  Another victory over the invading Shadowscythe forces!', 'quests/quest-RandomSoluna.swf', 1000, 1000),
(7, 7, 'Dropship Battle', 'Scanners are picking up strange signals on a nearby planet!  Hey you, with the red shirt, please go down there and investigate!', 'Congratulations!  You defeated the strange alien forces that were attacking the planet!', 'quests/quest-RandomDropship1.swf', 1000, 1000),
(8, 8, 'Test Town', '', '', 'towns/Soluna/town-SolunaCityNB-Frostval3015.swf', 0, 0),
(9, 9, 'Gray Void of Doom', 'Admin-lalalalalalalalalalalala!', '', 'towns/town-admin-FB5.swf', 0, 0),
(10, 10, 'Shuttle Test Piloting', 'Fly the dropship!', 'That was a lot of fun! Now you also know why Worsh is so nervous all of the time.', 'quests/quest-ShipMinigame-1.swf', 1000, 1000),
(11, 11, 'Strange Blip', 'Captain Sys-Zero has asked you to investigate the location of a strange blip on the radar for a nearby planet.', 'That strange blip was actually a powerful transforming alien mecha!', 'towns/intro/quest-StrangeBlip4.swf', 1000, 1000),
(12, 12, 'Pizza Pizza!', 'You have a mission critical  pizza that needs to be delivered before it gets cold!', 'Another satisfied customer! At Mecharoni the customer is always right. If not, try left!', 'towns/Pizza/quest-PizzaDelivery6.swf', 1000, 1000),
